mapper.insert:
/*fn : getBoardCnt, wr : 신현우, date: 2023.05.10*/
INSERT INTO BOARD (
	BOARD_SEQ  
	, BOARD_TITLE 
	, BOARD_CONTENT
	, BOARD_USE_YN
	, COM_CODE_SEQ
	, COM_CODE
	, REG_ID
	, REG_DT
) VALUES (
	${boardSeq} 
	, ${boardTitle} 
	, ${boardContent}
	, ${boardUseYn}
	, ${comCodeSeq}
	, ${comCode}
	, ${regId}
	, NOW()
)

mapper.update:
/*fn : getBoardCnt, wr : 신현우, date: 2023.05.10*/
UPDATE BOARD
SET	BOARD_TITLE = ${boardTitle} 
	, BOARD_CONTENT = ${boardContent}
	, COM_CODE_SEQ = ${comCodeSeq}
	, COM_CODE = ${comCode}
	, UPD_ID = ${updId}
	, UPD_DT = NOW()
WHERE BOARD_SEQ = ${boardSeq}

/*fn : getBoardCnt, wr : 신현우, date: 2023.05.10*/
UPDATE BOARD
SET	BOARD_USE_YN = 'Y'
WHERE BOARD_SEQ = ${boardSeq}

mapper.delete:
/*fn : getBoardCnt, wr : 신현우, date: 2023.05.10*/
DELETE 
FROM BOARD A
WHERE A.BOARD_SEQ = ${boardSeq}
